  	SELECT 
	re.id,re.money,re.type,re.detailed,re.date,re.processInstanceId,re.test,re.enclosure
	FROM reimbursement re
	LEFT JOIN user_reimbursement  ur ON ur.reimbursementId = re.id
	LEFT JOIN t_user tu ON tu.id = ur.userId

    	tu.id = #{userId}

	         AND test = #{state}


	         AND type = #{type}

SELECT re.id,re.money,re.type,re.detailed,re.date,re.processInstanceId,re.test,re.enclosure,aha.END_TIME_ as examinationTime, aha.START_TIME_ as startExaminationTime FROM reimbursement re LEFT JOIN act_hi_actinst aha ON aha.PROC_INST_ID_=re.processInstanceId
aha.ASSIGNEE_='admin'
AND test = #{state}
AND type = #{type}

    SELECT 
	re.id,re.money,re.type,re.detailed,re.date,re.processInstanceId,re.test,re.enclosure,ahv.LAST_UPDATED_TIME_ AS examinationTime
	FROM reimbursement re
	LEFT JOIN act_hi_varinst ahv ON ahv.PROC_INST_ID_=re.processInstanceId

		ahv.TEXT_=#{username}

	         AND test = #{state}


	         AND type = #{type}

