mapper.select:
  	SELECT
	 sm.id, tu.`name` AS userName, tu.headFile,sm.action,sm.text,sm.date,sm.state,bt.title,bt.id AS topicId
	FROM bbstopic bt
	JOIN systemmessage sm ON sm.topicId=bt.id
	JOIN t_user tu ON sm.userId = tu.id

		bt.userId=#{myId}

			AND sm.state = #{state}


			AND bt.typelike = #{type}
