mapper.insert:
insert into things (id, purpose, number, 
  date, name, state, 
  enclosure, filename, processInstanceId
  )
values (#{id,jdbcType=INTEGER}, #{purpose,jdbcType=VARCHAR}, #{number,jdbcType=INTEGER}, 
  #{date,jdbcType=TIMESTAMP}, #{name,jdbcType=VARCHAR}, #{state,jdbcType=VARCHAR}, 
  #{enclosure,jdbcType=VARCHAR}, #{filename,jdbcType=VARCHAR}, #{processinstanceid,jdbcType=VARCHAR}
  )

mapper.select:
   select 

   from things


	          state = #{state}


	         AND name = #{name}
